package string;

public class FactorialNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num =4;
		int factorialnumbe=1;
		
		for(int i = 1; i<=num;i++) {
			
			factorialnumbe=factorialnumbe*i;
		}
		
		
		System.out.println(factorialnumbe);
	}

}
